mapper.insert:
INSERT
INTO FR_FREE A(
    A.FR_NO
   ,A.FR_TITLE
   ,A.FR_DATE
   ,A.FR_VIEWS
   ,A.FR_CONTENTS
   ,A.MEM_NUM
   ) VALUES(
            'FR' || SEQ_FR_NO.NEXTVAL
                ,#{title}
                ,SYSDATE
                ,DEFAULT
                ,#{contents}
                ,#{mem_num}
    )

mapper.update:
update fr_free
set fr_views =  nvl(fr_views, 0) + 1
where fr_no = #{no}

UPDATE
    FR_FREE
SET FR_TITLE = #{ title }
   ,FR_CONTENTS = #{ contents }
WHERE FR_NO = #{ no }

mapper.delete:
DELETE
FROM FR_FREE
WHERE FR_NO = #{ no }

mapper.select:
SELECT A.RNUM , A.NB_NO , A.MEM_NUM , A.NB_TITLE , D.MEM_NAME , A.NB_DATE , A.NB_VIEWS
FROM (SELECT ROWNUM RNUM , B.NB_NO , B.NB_TITLE , B.NB_DATE , B.NB_VIEWS , B.MEM_NUM FROM (SELECT C.NB_NO , C.NB_TITLE , C.NB_DATE , C.NB_VIEWS , C.MEM_NUM FROM NB_NONE C
C.NB_TITLE LIKE '%' || #{ searchValue } || '%'
ORDER BY C.NB_DATE DESC ) B
WHERE ROWNUM <= #{ endRow }
) A LEFT JOIN MEMBER D ON(A.MEM_NUM = D.MEM_NUM) WHERE A.RNUM >= #{ startRow } ORDER BY 1 ASC

mapper.insert:
