mapper.select:
SELECT A.RNUM , A.MS_NO , A.MS_TITLE , A.MS_SEND_NAME , A.MS_RECP_NAME , A.MS_SEND_DATE , A.MS_RECP_DATE , A.MS_READ_YN , A.MEM_NUM , A.MS_SEND_DEL , A.MS_RECP_DEL , A.MS_RECP_NUM , A.MS_CONTENTS , A.MS_SEND_NUM FROM(SELECT ROWNUM RNUM , B.MS_NO , B.MS_TITLE , B.MS_SEND_NAME , B.MS_RECP_NAME , B.MS_SEND_DATE , B.MS_RECP_DATE , B.MS_READ_YN , B.MEM_NUM , B.MS_SEND_DEL , B.MS_RECP_DEL , B.MS_RECP_NUM , B.MS_CONTENTS , B.MS_SEND_NUM FROM( SELECT C.MS_NO , C.MS_TITLE , C.MS_SEND_NAME , C.MS_RECP_NAME , C.MS_SEND_DATE , C.MS_RECP_DATE , C.MS_READ_YN , C.MEM_NUM , C.MS_SEND_DEL , C.MS_RECP_DEL , C.MS_RECP_NUM , C.MS_CONTENTS , C.MS_SEND_NUM FROM MS_BOARD C
JOIN MEMBER D ON(C.MEM_NUM = D.MEM_NUM)
AND C.MS_TITLE LIKE '%' || #{ searchValue } || '%'
AND C.MS_SEND_NAME LIKE '%' || #{ searchValue } || '%'
AND C.MS_READ_YN = 'N' AND C.MEM_NUM = C.MS_RECP_NUM AND C.MS_RECP_DEL = 'N'
ORDER BY C.MS_NO DESC )B
WHERE ROWNUM <= #{ endRow }
) A WHERE A.RNUM >= #{ startRow }

SELECT A.RNUM , A.MS_NO , A.MS_TITLE , A.MS_SEND_NAME , A.MS_RECP_NAME , A.MS_SEND_DATE , A.MS_RECP_DATE , A.MS_READ_YN , A.MEM_NUM , A.MS_SEND_DEL , A.MS_RECP_DEL , A.MS_RECP_NUM , A.MS_CONTENTS , A.MS_SEND_NUM FROM(SELECT ROWNUM RNUM , B.MS_NO , B.MS_TITLE , B.MS_SEND_NAME , B.MS_RECP_NAME , B.MS_SEND_DATE , B.MS_RECP_DATE , B.MS_READ_YN , B.MEM_NUM , B.MS_SEND_DEL , B.MS_RECP_DEL , B.MS_RECP_NUM , B.MS_CONTENTS , B.MS_SEND_NUM FROM(SELECT C.MS_NO , C.MS_TITLE , C.MS_SEND_NAME , C.MS_RECP_NAME , C.MS_SEND_DATE , C.MS_RECP_DATE , C.MS_READ_YN , C.MEM_NUM , C.MS_SEND_DEL , C.MS_RECP_DEL , C.MS_RECP_NUM , C.MS_CONTENTS , C.MS_SEND_NUM FROM MS_BOARD C
C.MS_TITLE = #{ searchValue }
C.MS_SEND_NAME LIKE '%' || #{ searchValue } || '%'
C.MS_RECP_NAME LIKE '%' || #{ searchValue } || '%'
AND C.MEM_NUM = C.MS_SEND_NUM AND C.MS_SEND_DEL = 'N'
ORDER BY C.MS_NO DESC )B
WHERE ROWNUM <= #{ endRow }
) A WHERE A.RNUM >= #{ startRow }

SELECT A.RNUM , A.MS_NO , A.MS_TITLE , A.MS_SEND_NAME , A.MS_RECP_NAME , A.MS_SEND_DATE , A.MS_RECP_DATE , A.MS_READ_YN , A.MEM_NUM , A.MS_SEND_DEL , A.MS_RECP_DEL , A.MS_RECP_NUM , A.MS_CONTENTS , A.MS_SEND_NUM FROM(SELECT ROWNUM RNUM , B.MS_NO , B.MS_TITLE , B.MS_SEND_NAME , B.MS_RECP_NAME , B.MS_SEND_DATE , B.MS_RECP_DATE , B.MS_READ_YN , B.MEM_NUM , B.MS_SEND_DEL , B.MS_RECP_DEL , B.MS_RECP_NUM , B.MS_CONTENTS , B.MS_SEND_NUM FROM(SELECT C.MS_NO , C.MS_TITLE , C.MS_SEND_NAME , C.MS_RECP_NAME , C.MS_SEND_DATE , C.MS_RECP_DATE , C.MS_READ_YN , C.MEM_NUM , C.MS_SEND_DEL , C.MS_RECP_DEL , C.MS_RECP_NUM , C.MS_CONTENTS , C.MS_SEND_NUM FROM MS_BOARD C
C.MS_TITLE LIKE '%' || #{ searchValue } || '%'
C.MS_SEND_NAME LIKE '%' || #{ searchValue } || '%'
C.MS_RECP_NAME LIKE '%' || #{ searchValue } || '%'
AND C.MEM_NUM = C.MS_RECP_NUM AND C.MS_RECP_DEL = 'N'
ORDER BY C.MS_NO DESC )B
WHERE ROWNUM <= #{ endRow }
) A WHERE A.RNUM >= #{ startRow }

mapper.insert:
INSERT 
INTO MS_BOARD A
(
  A.MS_NO
, A.MS_TITLE
, A.MS_CONTENTS
, A.MS_SEND_NUM
, A.MS_SEND_NAME
, A.MS_RECP_NUM
, A.MS_SEND_DATE
, A.MEM_NUM
)
VALUES
(
SEQ_MS_NO.NEXTVAL
, #{ msTitle }
, #{ contents }
, #{ sendNum }
, #{ sendName }
, #{ recpNum }
, SYSDATE
, #{ memNum }
)

mapper.delete:
DELETE
FROM MS_FILE
WHERE MS_NO = #{ msNo }
