mapper.select:
SELECT A.RNUM , A.SIGN_NO , A.SIGN_TITLE , A.SIGN_WRITER , A.REG_DATE , D.MEM_NAME , G.DEPT_NAME , A.SF_NAME FROM (SELECT ROWNUM RNUM , B.SIGN_NO , B.SIGN_TITLE , B.SIGN_WRITER , B.REG_DATE , B.SF_NAME FROM (SELECT C.SIGN_NO , C.SIGN_TITLE , C.SIGN_WRITER , C.REG_DATE , E.SF_NAME FROM SI_SIGN C JOIN APPROVER F ON(C.SIGN_NO = F.SIGN_NO) JOIN SF_SIGN_FORM E ON(C.SIGN_FORM = E.SF_CODE)
JOIN MEMBER D ON (C.SIGN_WRITER = D.MEM_NUM)
AND D.MEM_NAME LIKE '%' || #{ searchWriter } || '%'
AND E.SF_NAME LIKE '%' || #{ searchForm } || '%'
AND C.SIGN_TITLE LIKE '%' || #{ searchTitle } || '%'
AND C.REG_DATE BETWEEN #{ searchStartDate } AND #{ searchEndDate }
AND C.SIGN_NO = #{ searchNum }
AND F.APPROVER_NO = #{ mem_num } AND F.IS_APPROVED = 'N' AND C.IS_TEMP = 'N'
ORDER BY C.SIGN_NO DESC ) B
WHERE ROWNUM <= #{ selectCriteria.endRow }
) A JOIN MEMBER D ON (A.SIGN_WRITER = D.MEM_NUM) JOIN DEPARTMENT G ON(D.DEPT_CODE = G.DEPT_CODE) WHERE A.RNUM >= #{ selectCriteria.startRow } ORDER BY 1 ASC

SELECT A.RNUM , A.SF_CODE , A.SF_NAME FROM (SELECT ROWNUM RNUM , B.SF_CODE , B.SF_NAME FROM (SELECT C.SF_CODE , C.SF_NAME FROM SF_SIGN_FORM C
C.SF_NAME LIKE '%'|| #{ searchName } || '%'
ORDER BY C.SF_CODE ASC) B
WHERE ROWNUM <= #{ selectCriteria.endRow }
) A WHERE A.RNUM >= #{ selectCriteria.startRow }

